import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation, useParams } from 'react-router-dom';

const QuizPage = () => {
  const params = useParams();
  const category = params.categoryId;
  const navigate = useNavigate();

  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [correctCount, setCorrectCount] = useState(0);
  const [unansweredCount, setUnansweredCount] = useState(0);
  const [timeLeft, setTimeLeft] = useState(10);
  const [showAnswer, setShowAnswer] = useState(false);

  const questions = category?.questions || [];
  const currentQuestion = questions[currentQuestionIdx];

  useEffect(() => {
    if (timeLeft === 0) {
      handleNext();
      return;
    }
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft]);

  const handleAnswer = (answer) => {
    if (selectedAnswer) return;
    setSelectedAnswer(answer);
    setShowAnswer(true);
    if (answer[0] === currentQuestion.correctAnswer) {
      setCorrectCount((prev) => prev + 1);
    }
  };

  const handleNext = () => {
    if (!selectedAnswer) {
      setUnansweredCount((prev) => prev + 1);
    }
    if (currentQuestionIdx + 1 < questions.length) {
      setCurrentQuestionIdx((idx) => idx + 1);
      setSelectedAnswer(null);
      setShowAnswer(false);
      setTimeLeft(currentQuestion.timeLimit);
    } else {
      navigate('/result', {
        state: { correct: correctCount, unanswered: unansweredCount }
      });
    }
  };

  if (!category) return <p>No category selected.</p>;

  return (
    <div className="container">
      <div className="header">
        <h2>Question {currentQuestionIdx + 1} of {questions?.length}</h2>
        <span className="timer">‚è± {timeLeft}s</span>
      </div>
      <p className="question">{currentQuestion?.question}</p>
      <div className="options">
        {currentQuestion.options.map((option) => {
          const isCorrect = option[0] === currentQuestion.correctAnswer;
          const isSelected = selectedAnswer === option;
          return (
            <button
              key={option}
              onClick={() => handleAnswer(option)}
              className={`option-btn ${isSelected ? (isCorrect ? 'correct' : 'incorrect') : ''}`}
              disabled={!!selectedAnswer}
            >
              {option}
            </button>
          );
        })}
      </div>
      <button onClick={handleNext} className="btn next-btn">Next</button>
    </div>
  );
};

export default QuizPage;
